
var Image = require("./index");

var image = new Image();
var fs = require("fs");
var buf = fs.createReadStream("/Users/florent/Desktop/200.jpeg");
image.setData(buf).then(function(image){
	//console.log("Set");
	return image.pad({
		left: 5,
		top: 10,
		right: 20,
		bottom: 40,
		color: "#F00"
	})
}).then(function(image){
	console.log("pad");
	return image.data()
}).then(function(data){
	console.log(data);
	fs.writeFile("/Users/florent/Desktop/200-pad.jpeg", data)
}).fail(function(err){
	console.error(err);
})